// Extract the clients array from GraphQL response
const clients = $input.all()[0].json.data.getClientList.clients;

// Transform to standard format with platform identifier
return clients.map(client => ({
  json: {
    platform: 'SuperOps',
    id: client.accountId,
    name: client.name
  }
}));

// Extract companies array from Hudu response
const companies = $input.all()[0].json.companies;

// Transform to standard format
return companies.map(company => ({
  json: {
    platform: 'Hudu',
    id: company.id,
    name: company.name
  }
}));

// Get the raw input items
const items = $input.all();

// Map through each item (NinjaOne returns multiple items, one per org)
return items.map(item => ({
  json: {
    platform: 'NinjaOne',
    id: item.json.id,
    name: item.json.name
  }
}));

// Huntress wraps in an organizations array
const orgs = $input.all()[0].json.organizations;

// Transform to standard format
return orgs.map(org => ({
  json: {
    platform: 'Huntress',
    id: org.id,
    name: org.name
  }
}));

// DNS Filter wraps in a data array
const orgs = $input.all()[0].json.data;

// Transform to standard format
return orgs.map(org => ({
  json: {
    platform: 'DNS Filter',
    id: org.id,
    name: org.attributes.name
  }
}));

// Get all items
const items = $input.all();

// Simple string similarity function (Levenshtein-based)
function similarity(s1, s2) {
  const longer = s1.length > s2.length ? s1 : s2;
  const shorter = s1.length > s2.length ? s2 : s1;

  if (longer.length === 0) return 1.0;

  const editDistance = levenshteinDistance(longer, shorter);
  return (longer.length - editDistance) / longer.length;
}

function levenshteinDistance(s1, s2) {
  const costs = [];
  for (let i = 0; i <= s1.length; i++) {
    let lastValue = i;
    for (let j = 0; j <= s2.length; j++) {
      if (i === 0) {
        costs[j] = j;
      } else if (j > 0) {
        let newValue = costs[j - 1];
        if (s1.charAt(i - 1) !== s2.charAt(j - 1)) {
          newValue = Math.min(Math.min(newValue, lastValue), costs[j]) + 1;
        }
        costs[j - 1] = lastValue;
        lastValue = newValue;
      }
    }
    if (i > 0) costs[s2.length] = lastValue;
  }
  return costs[s2.length];
}

// Normalize names for better matching
function normalizeName(name) {
  return name
    .toLowerCase()
    .replace(/[^\w\s]/g, '') // Remove punctuation
    .replace(/\s+/g, ' ')    // Normalize whitespace
    .trim();
}

// Group by similar names
const groups = {};
const threshold = 0.85; // 85% similarity required

items.forEach(item => {
  const normalizedName = normalizeName(item.json.name);
  let matched = false;

  // Try to match with existing groups
  for (const groupKey in groups) {
    const groupNormalizedName = normalizeName(groupKey);
    const score = similarity(normalizedName, groupNormalizedName);

    if (score >= threshold) {
      // Add to existing group
      groups[groupKey].items.push(item.json);
      matched = true;
      break;
    }
  }

  // If no match, create new group
  if (!matched) {
    groups[item.json.name] = {
      canonicalName: item.json.name,
      items: [item.json]
    };
  }
});

// Transform groups into output format
const results = Object.values(groups).map(group => {
  const platformData = {};

  group.items.forEach(item => {
    platformData[item.platform] = item.id;
  });

  return {
    json: {
      name: group.canonicalName,
      platforms: Object.keys(platformData),
      platformCount: Object.keys(platformData).length,
      ...platformData
    }
  };
});

return results;

const items = $input.all();

// Sort by platform count (most platforms first)
const sorted = items.sort((a, b) => b.json.platformCount - a.json.platformCount);

// Build HTML table
let html = `
<!DOCTYPE html>
<html>
<head>
  <style>
    body { font-family: Arial, sans-serif; margin: 20px; }
    table { border-collapse: collapse; width: 100%; }
    th, td { border: 1px solid #ddd; padding: 12px; text-align: left; }
    th { background-color: #4CAF50; color: white; }
    tr:nth-child(even) { background-color: #f2f2f2; }
    .full-match { background-color: #d4edda; }
    .partial-match { background-color: #fff3cd; }
    .single-platform { background-color: #f8d7da; }
  </style>
</head>
<body>
  <h1>Platform Organization Comparison</h1>
  <p>Total unique organizations: ${items.length}</p>
  <table>
    <thead>
      <tr>
        <th>Company Name</th>
        <th>Platforms</th>
        <th>Count</th>
        <th>SuperOps ID</th>
        <th>Hudu ID</th>
        <th>NinjaOne ID</th>
        <th>Huntress ID</th>
        <th>DNS Filter ID</th>
      </tr>
    </thead>
    <tbody>
`;

sorted.forEach(item => {
  const rowClass = 
    item.json.platformCount === 5 ? 'full-match' :
    item.json.platformCount > 1 ? 'partial-match' :
    'single-platform';

  html += `
      <tr class="${rowClass}">
        <td>${item.json.name}</td>
        <td>${item.json.platforms.join(', ')}</td>
        <td>${item.json.platformCount}</td>
        <td>${item.json.SuperOps || '-'}</td>
        <td>${item.json.Hudu || '-'}</td>
        <td>${item.json.NinjaOne || '-'}</td>
        <td>${item.json.Huntress || '-'}</td>
        <td>${item.json['DNS Filter'] || '-'}</td>
      </tr>
  `;
});

html += `
    </tbody>
  </table>
</body>
</html>
`;

return [{
  json: { html },
  binary: {
    data: {
      data: Buffer.from(html).toString('base64'),
      mimeType: 'text/html',
      fileName: 'platform-comparison.html'
    }
  }
}];

const items = $input.all();

// Sort by platform count (most platforms first)
const sorted = items.sort((a, b) => b.json.platformCount - a.json.platformCount);

// CSV header
let csv = 'Company Name,Platforms,Platform Count,SuperOps ID,Hudu ID,NinjaOne ID,Huntress ID,DNS Filter ID\n';

// CSV rows
sorted.forEach(item => {
  const row = [
    `"${item.json.name}"`,
    `"${item.json.platforms.join(', ')}"`,
    item.json.platformCount,
    item.json.SuperOps || '',
    item.json.Hudu || '',
    item.json.NinjaOne || '',
    item.json.Huntress || '',
    item.json['DNS Filter'] || ''
  ];

  csv += row.join(',') + '\n';
});

return [{
  json: { 
    csv,
    rowCount: items.length 
  },
  binary: {
    data: {
      data: Buffer.from(csv).toString('base64'),
      mimeType: 'text/csv',
      fileName: 'platform-comparison.csv'
    }
  }
}];

// Transform comparison results for ID Mapping
// Hudu is canonical source - process ALL Hudu companies
const companies = $input.all();

return companies
  .filter(company => company.json.Hudu) // Only companies that exist in Hudu
  .map(company => {
    return {
      json: {
        company_name: company.json.name,
        hudu_id: company.json.Hudu,
        superops_id: company.json.SuperOps || null,
        ninjaone_id: company.json.NinjaOne || null,
        huntress_id: company.json.Huntress || null,
        dnsfilter_id: company.json["DNS Filter"] || null
      }
    };
  });

